<script type="text/javascript">/* <![CDATA[ */
    var imgBase64TransparentSrc = 'data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7';
    adsLayoutIsDetal     = true;
    adsLayoutIsSpecialTopic = false;
    
    adsNoBanner          = false;
    
    
    adsExclusive         = false;
    
    onetAds={
        'target': 'BUSINESSINSIDER/GLOWNA',
        'async': 1,
        'sgn_cookie': 1,
        'autoslot': 1,
        'ver': 2,
        'cmd': [
            function (){
                dlApi.registerTemplate({
                    tplCode: "1746213/Teaser-Standard",
                    renderAd: function (Ad, dlApi, window){
                        var el = document.getElementById(Ad.div);
                        if(!el){
                            return false
                        }
                        prepareLpsNatLeft(el, Ad);
                        initNatLeft2(el, Ad);
                        
                        var lpsItemListLinks = el.querySelectorAll(".lpsItem a");
                        var lpsItemListLabel = el.querySelector(".lpsItem .title");
                        var img = el.querySelector(".lpsItem img");
                        var partnerLogo = el.querySelector(".lpsItem .partnerLogo");
                        var sponsored = el.querySelector(".lpsItem .sponsored");
                        var listData = el.querySelector(".flex.byline");
                        var listItemHref = el.querySelector(".river .lpsItem .span8 .feature-post .title");
                        var bigImgAd = null;
                        
                        if(partnerLogo!==null){
                            if(Ad.fields.partner_logo){
                                partnerLogo.innerHTML='<img src="'+Ad.fields.partner_logo+'"/>';
                                partnerLogo.removeAttribute("hidden");
                            }else{
                                partnerLogo.innerHTML='';
                            }
                        }
                        
                        if(el.className.indexOf('bigpicture')>=0){
                            bigImgAd = el;
                        }
                        if(img && Ad.fields.image){
                            if(img.dataset.original){
                                img.dataset.original = Ad.fields.image;
                            }else{
                                img.src = Ad.fields.image;
                            }
                        }
                        if (lpsItemListLinks) {
                            for (var i = 0; i < lpsItemListLinks.length; i++)
                            {
                                lpsItemListLinks[i].href = Ad.meta.adclick + Ad.fields.url;
                            }
                        }
                        
                        if(lpsItemListLabel){
                            lpsItemListLabel.innerHTML = Ad.fields.title;
                        }
                        
                        if(img){
                            img.parentNode.href=Ad.meta.adclick + Ad.fields.url;
                            img.alt = Ad.fields.title;
                        }

                        if(listData!==null){
                            listData.parentNode.removeChild(listData);
                        }
                        if(listItemHref!==null){
                            listItemHref.href = Ad.meta.adclick + Ad.fields.url;
                        }
                        if(sponsored!==null){
                            sponsored.innerHTML = Ad.fields.adInfo||"";
                            sponsored.removeAttribute("hidden");
                        }
                        if(bigImgAd!==null){
                            bigImgAd.querySelector(".overridable").className+=(" nativeRight");
                            bigImgAd.className+=(" lpsItemSet");
                        }
                        
                        el.className += ' lpsInitialized';
                    }
                });
            }
        ],
        'adSlots': [],
        'no_gemius':  1,
        'keywords': [ ],
        'old_dot': true,
        
        'gfkMediaID':'BusinessinsiderComPL',
        
        
        'mobile': 0,
        
        'DV': 'BUSINESSINSIDER/glowna',
        
        'keyvalues': {},
        
        'structure': 'glowna',
        
        
            'lazy': 1,
            
            'lazyPercentage':  100,
            
        
        

        
        'kropka': {
            
        }
    };

    
    
    
        onetAds.keywords.push('home');
    
        onetAds.keywords.push('business-insider');
    

    if (typeof identifierEI !== "undefined"){
        onetAds.kropka['EI'] = identifierEI;
    }
    


    if (onetAds && onetAds.keyvalues && onetAds.keyvalues.ci) {
      if( window.sessionStorage ){
         if( window.sessionStorage.getItem("galClose"+onetAds.keyvalues.ci) == "true" ){
            onetAds.kropka.DX = onetAds.kropka.DX.replace('.','')+',y46t97.';
            var clear = window.sessionStorage.removeItem("galClose"+onetAds.keyvalues.ci);
         }
      }
      else{
         var c_value = document.cookie;
         var c_name = "galClose"+onetAds.keyvalues.ci;
         var c_start = c_value.indexOf(" " + c_name + "=");
         if (c_start == -1) {
            c_start = c_value.indexOf(c_name + "=");
         }
         if (c_start == -1){
            c_value = null;
         }
         else {
            c_start = c_value.indexOf("=", c_start) + 1;
            var c_end = c_value.indexOf(";", c_start);
            if (c_end == -1) {
               c_end = c_value.length;
            }
            c_value = unescape(c_value.substring(c_start,c_end));
         }
         if(c_value){
            onetAds.kropka.DX = onetAds.kropka.DX.replace('.','')+',y46t97.';
            document.cookie = encodeURIComponent("galClose"+onetAds.keyvalues.ci) + "=deleted; expires=" + new Date(0).toUTCString();
         }
      }

    }
    
    function createSlots(selector, slotName, count, imgWidth, imgHeight, fetch, promoted, countFrom) {
        var domElement = document.querySelectorAll(selector);
        if (domElement && domElement.length > 0) {
            if (domElement.length < count || count === false) {
                count = domElement.length;
            }
            if(!countFrom){
                countFrom = 0;
            }
            for (var i = 0; i < count; i++) {
                var itemId = slotName + '-' + (i + 1 + countFrom);
                domElement[i].id = itemId;
                var slot = onetAds.defineSlot(slotName, itemId);
                if(!!promoted){
                    slot.setTargeting('section', 'promoted');
                }
                slot.setTargeting('pos', i + 1 + countFrom);
                slot.imgTransform('resizecropauto', {width: imgWidth, height: imgHeight});
            }
        }

        if (!!fetch)
        {
            onetAds.fetch();
        }
    }
    
    function initNatLeft2(el, Ad) {
        if (el.className.match('streamLine')) {
            var itemCollageImg = el.querySelector('.itemCollage');
            if (itemCollageImg) {
                var img = itemCollageImg.querySelector('img');
                if (img) {
                    img.src = Ad.fields.image ? Ad.fields.image : imgBase64TransparentSrc;
                }
                itemCollageImg.addEventListener("load", function (e) {
                    setTimeout(function () {
                        var img = itemCollageImg.querySelector('img');
                        if (img) {
                            img.src = Ad.fields.image ? Ad.fields.image : imgBase64TransparentSrc;
                        }
                    }, 50);
                });
            }
            if (Ad.fields.partner_logo || Ad.fields.adInfo) {
                var partnerWrapperDiv = document.createElement('div');
                partnerWrapperDiv.className = 'partnerWrapper';
                el.appendChild(partnerWrapperDiv);
                
                if (Ad.fields.partner_logo) {
                    var partnerDiv = document.createElement('div');
                    partnerDiv.className = 'partnerLogo';
                    partnerWrapperDiv.appendChild(partnerDiv);
                }
                if (Ad.fields.adInfo) {
                    var partnerDiv = document.createElement('div');
                    partnerDiv.className = 'sponsored';
                    partnerWrapperDiv.appendChild(partnerDiv);
                }
            }
            
        }
       
    }
    
    document.addEventListener("DOMContentLoaded", function(){
        createSlots('.recomendedWidget .lpsItem', 'flat-natright', false, 60, 45, false);
        if(!isMobile){
            createSlots('.relatedBottom .seeAlsoBottom', 'flat-natleft2', false, 215, 170, false);
        }
        createSlots('.lpsNatLeft', 'flat-natleft', false, 265, 199, false);
        createSlots('.lpsNatLeft2 .streamNews .lpsItem', 'flat-natleft2', 12, 270, 151, 1, false);
        
        //mobile detail
        createSlots('.sH_mobile_bottom_first .concierge-post', 'flat-natleft2', false, 120, 95, false);
    }, false);

    function prepareLpsNatLeft(el, Ad) {
        if (el.className.match('lpsNatLeft')) {
            var parent = el.parentNode;
            var templateEl = parent.querySelector('.lpsNatLeftTemplate');
            if (templateEl) {
                el.innerHTML = templateEl.innerHTML;
                el.className += ' ' + templateEl.className + ' lpsNatLeftItem';
                var sponsoredMobileToRemove = el.querySelector('.river-post__heading .sponsored.hidden');
                if(sponsoredMobileToRemove){
                    sponsoredMobileToRemove.remove();
                }
            }
        }
    }
        

</script>


